mapper.select:
select bIdx,
       recCountMax from ( select bIdx, count(*) as recCountMax from rec where bIdx in  #{bIdx} 
       and isRecommended = 'Y' group by bIdx ) as countResults order by recCountMax desc limit 1;

select bIdx from
       book where bmIdx = #{bmIdx} and bsIdx = #{bsIdx} and bssIdx = #{bssIdx} and bsssIdx =
       #{bsssIdx}
